import enum


class Gender(enum.IntEnum):
    """
    用户性别

    Note:
        UNKNOWN 未知\n
        MALE 男性\n
        FEMALE 女性
    """

    UNKNOWN = 0
    MALE = 1
    FEMALE = 2


class PrivLike(enum.IntEnum):
    """
    关注吧列表的公开状态

    Note:
        PUBLIC 所有人可见\n
        FRIEND 好友可见\n
        HIDE 完全隐藏
    """

    PUBLIC = 1
    FRIEND = 2
    HIDE = 3


class PrivReply(enum.IntEnum):
    """
    帖子评论权限

    Note:
        ALL 允许所有人\n
        FANS 仅允许我的粉丝\n
        FOLLOW 仅允许我的关注
    """

    ALL = 1
    FANS = 5
    FOLLOW = 6


class ReqUInfo(enum.Flag):
    """
    使用该枚举类指定待获取的用户信息字段

    Note:
        各bit位的含义由高到低分别为 OTHER, TIEBA_UID, NICK_NAME, USER_NAME, PORTRAIT, USER_ID\n
        其中BASIC = USER_ID | PORTRAIT | USER_NAME
    """

    USER_ID = enum.auto()
    PORTRAIT = enum.auto()
    USER_NAME = enum.auto()
    NICK_NAME = enum.auto()
    TIEBA_UID = enum.auto()
    OTHER = enum.auto()
    BASIC = USER_ID | PORTRAIT | USER_NAME
    ALL = BASIC | NICK_NAME | TIEBA_UID | OTHER


class ThreadSortType(enum.IntEnum):
    """
    主题帖排序

    Note:
        对于有热门分区的贴吧 0热门排序(HOT) 1按发布时间(CREATE) 2关注的人(FOLLOW) 34热门排序(HOT) >=5是按回复时间(REPLY)\n
        对于无热门分区的贴吧 0按回复时间(REPLY) 1按发布时间(CREATE) 2关注的人(FOLLOW) >=3按回复时间(REPLY)
    """

    REPLY = 5
    CREATE = 1
    HOT = 3
    FOLLOW = 2


class PostSortType(enum.IntEnum):
    """
    回复排序

    Note:
        ASC 时间顺序\n
        DESC 时间倒序\n
        HOT 热门序
    """

    ASC = 0
    DESC = 1
    HOT = 2


class SearchType(enum.IntEnum):
    """
    搜索类型

    Note:
        ALL 搜索全部\n
        TIME app时间倒序\n
        RELATION app相关性排序
    """

    ALL = 0
    TIME = 1
    RELATION = 2


class WsStatus(enum.IntEnum):
    """
    回复排序

    Note:
        CLOSED 已关闭\n
        CONNECTING 正在连接\n
        OPEN 可用
    """

    CLOSED = 0
    CONNECTING = enum.auto()
    OPEN = enum.auto()


class GroupType(enum.IntEnum):
    """
    消息组类型
    """

    PRIVATE_MSG = 6
    MISC = 8


class MsgType(enum.IntEnum):
    """
    消息类型
    """

    PRIVATE_MSG = 1
    MISC = 10
    READED = 22
